export const DateFormatter = (unixDate) => {
  const today = new Date().toLocaleString("en-GB", {
    weekday: "long",
    year: "numeric",
    month: "long",
    day: "numeric",
  });
  const newDate = new Date(unixDate * 1000);
  const dateLong = newDate.toLocaleString("en-GB", {
    weekday: "long",
    year: "numeric",
    month: "long",
    day: "numeric",
  });
  console.log(newDate);
  const time = newDate
    .toString()
    .split("")
    .reverse()
    .join("")
    .slice(45, 49)
    .split("")
    .reverse()
    .join("");
  if (today === dateLong) {
    return { date: "Today", time: time, newDate: newDate };
  } else {
    return { date: dateLong, time: time, newDate: newDate };
  }
};

export const getMonthDay = (dateArr) => {
  const dayMonthTimeArr = [];
  return dateArr.map((unixDate) => {
    const date = new Date(unixDate * 1000);
    const time = date
      .toString()
      .split("")
      .reverse()
      .join("")
      .slice(43, 48)
      .split("")
      .reverse()
      .join("");
    const month = date.getMonth() + 1;
    const weekday = date.toLocaleDateString("en-GB", { weekday: "short" });
    const day = date.getDay() + 1;
    let str = `${weekday} ${day}/${month} ${time}`;
    dayMonthTimeArr.push(str);
  });
  return dayMonthTimeArr;
};
